use Northwind--tampilkan 3 data  supplier yang terdiri dari nama perusahaan, kota dan negara sebagai alamat lengkap perusahaan,--nama produk yang di supply, serta harga rata-rata produk di negara Norway,Denmark,Finland, dan Spain --dan diurutkan berdasarkan harga rata-rata produk yang paling tinggiselect * from Suppliersselect * from Productsselect top 3 s.CompanyName, CONCAT(s.Country,', ', s.City) AlamatPerusahaan, p.ProductName, AVG(UnitPrice) Rata2Produkfrom Suppliers sjoin products p on s.SupplierID = p.SupplierIDwhere Country in ('Norway', 'Denmark', 'Finland', 'Spain')group by s.Country, s.CompanyName, s.City, p.ProductNameorder by Rata2Produk desc--Buatlah laporan total ongkos kirim (freight) untuk masing-masing shipper company dalam satu tahun pada --tahun (1996, 1997, 1998) dengan nilai total ongkir pembulatan ke atas tanpa angka nol di belakang koma --1996, 1997, 1998 (sumbu x)--List shipper company name (sumbu y)select * from Shippersselect * from Ordersselect * from (select s.CompanyName, convert(int,CEILING(sum(o.Freight))) OngkosKirim, DATENAME(YEAR,o.ShippedDate) Tahunfrom Shippers sjoin Orders o on s.ShipperID = o.ShipViawhere DATENAME(YEAR,o.ShippedDate) between 1996 and 1998 -- and DATENAME(YEAR,o.ShippedDate) is not nullgroup by s.CompanyName, o.ShippedDate, o.Freight) tblPIVOT(sum(OngkosKirim) for Tahun in ([1996],[1997],[1998])) pvt--Seorang bos ingin mempromosikan seorang pegawai dimana pegawai tersebut sudah bekerja selama 8 tahun atau lebih dan berumur 40 tahun atau lebih dan karyawan --tersebut harus berasal kota yg terletak pada eastern atau western region juga jabatan karyawan tersebut bukan manager/vice president.--NOTE : (tampilkan nama panjangnya saja) (tahun patokan yg dipakai adalah 2000)select * from Employeesselect * from Regionselect CONCAT(e.FirstName, ' ', e.LastName) FullNamefrom Employees ejoin EmployeeTerritories et on e.EmployeeID = et.EmployeeIDjoin Territories t on t.TerritoryID = et.TerritoryIDjoin Region r on r.RegionID = t.RegionIDwhere DATEDIFF( Year, e.HireDate, '2000') >= 8 and DATEDIFF( Year, e.BirthDate, '2000') >= 40 and r.RegionDescription in ('Eastern','Western') ande.Title not like '%Manager%' and e.Title not like '%Vice President%'group by e.FirstName, e.LastName--Tampilkan nama kontak dan nomer telepon customer yang pernah membeli produk yang stoknya habis dan sudah diskontinuselect * from Customersselect * from Productsselect distinct c.ContactName, c.Phonefrom Customers cjoin Orders o on c.CustomerID = o.CustomerIDjoin [Order Details] od on o.OrderID = od.OrderIDjoin Products p on od.ProductID = p.ProductIDwhere p.UnitsInStock = 0 and p.Discontinued = 1order by c.ContactName asc--Keluarkan Contact name Customer dan full name employee yang menghandle customer tersebut dan juga berapa total order dari customer.--Employee Berasal dari Country USA, dan employee yang dikeluarkan yang sudah bekerja 5 tahun/1825 hari saat ship orderselect * from Customersselect * from Employeesselect * from Ordersselect c.ContactName, CONCAT(e.FirstName, ' ', e.LastName) FullName, COUNT(1) TotalOrderfrom Customers cjoin Orders o on c.CustomerID = o.CustomerIDjoin Employees e on o.EmployeeID = e.EmployeeIDwhere e.Country = 'USA' and DATEDIFF(YEAR, e.HireDate, o.ShippedDate) >= 5 -- or DATEDIFF(DAY, o.ShippedDate, GETDATE()) = 1825group by c.ContactName, CONCAT(e.FirstName, ' ', e.LastName)--Tuliskan query SQL untuk menemukan 1 produk yang memiliki harga rata-rata tertinggi di antara produk-produk yang memiliki stok di bawah rata-rataselect * from Ordersselect * from Productsselect p.ProductName, AVG(p.UnitPrice) Rata2Harga, AVG(p.UnitsInStock) Rata2Stokfrom Products pgroup by p.ProductNameorder by Rata2Harga desc, Rata2Stok asc;with RataHarga as (select AVG(p.UnitPrice) HargaRatafrom Products p ),RataStok as (select AVG(p.UnitsInStock) Stokfrom Products p)select top 1 p.ProductName, p.UnitPrice, p.UnitsInStockfrom RataHarga, RataStok, Products pwhere p.UnitPrice > HargaRata and p.UnitsInStock < Stokorder by HargaRata desc, Stok asc--TAMPILKAN NAMA PRODUK, NAMA KATEGORI PRODUK DARI MAKANAN YANG MEMILIKI RASA MANIS --DAN MEMILIKI HARGA DI URUTAN NO 3 DARI YANG TERTINGGIselect * from Productsselect * from Categoriesselect p.ProductName, c.CategoryNamefrom Categories cjoin Products p on c.CategoryID = p.CategoryIDwhere c.Description like '%sweet%'order by p.UnitPrice descoffset 2 rows fetch next 1 rows only/*1.Tolong Buatkan Query untuk mengambil TOP 3 data, dengan EmployeeID (2,3,5,7), pada Tahun 1998 saja. Dan yang ditampilkan adalah : - EmployeeIDnya Dengan nama [ID Karyawan]- Nama Perusahaan ShipVia Dengan nama [Nama Perusahaan]- ShipCountry- UnitPriceDiurutkan berdasarkan ShipCountry dari yang terkecil (A - Z) dan UnitPrice terbesar*/select * from Employeesselect * from Shippersselect * from Ordersselect * from Productsselect top 3 CONCAT(e.EmployeeID, ' ',e.FirstName, ' ', e.LastName) FullName, s.CompanyName, o.ShipCountry, p.UnitPricefrom Employees ejoin Orders o on e.EmployeeID = o.EmployeeIDjoin Shippers s on o.ShipVia = s.ShipperIDjoin [Order Details] od on od.OrderID = o.OrderIDjoin Products p on p.ProductID = od.ProductIDwhere e.EmployeeID in (2,3,5,7) and o.OrderDate = '1998'order by o.ShipCountry asc, p.UnitPrice desc--Keluarkan sapaan beserta nama lengkap (C1) dan umur ketika di-hire dalam tahun (C2) untuk dua orang pegawai --yang ketika di-hire berusia paling tua (R1) dan berusia paling muda (R2). Sehingga dihasilkan 2 buah kolom (C)--dan 2 buah baris (R)select * from Employees;with tbl1 as (select top 1 CONCAT(e.FirstName, ' ', e.LastName) NamaLengkap, DATEDIFF(YEAR, e.BirthDate, e.HireDate) Usiafrom Employees eorder by Usia desc),tbl2 as (select top 1 CONCAT(e.FirstName, ' ', e.LastName) NamaLengkap, DATEDIFF(YEAR, e.BirthDate, e.HireDate) Usiafrom Employees eorder by Usia asc)select * from tbl1union allselect * from tbl2